import React, {Component} from 'react';

var SpotifyWebApi = require('spotify-web-api-node');

class FirstPage extends Component{
    constructor(props){
        super(props);

        this.state = {
            access_token: '',
            client_id: '',
            client_secret: '',
            user_image: '',
            user_name: ''
        };

        this.getMe();
    }

    componentDidMount(){
        const params = this.getHashParams();

        this.setState({
            access_token : params.access_token,
            client_id : params.client_id,
            client_secret : params.client_secret,
        })

        var spotifyApi = new SpotifyWebApi({
            clientId: this.state.client_id,
            clientSecret: this.state.client_secret,
            redirectUri: 'http://www.example.com/callback'
        });

        spotifyApi.setAccessToken(this.access_token);
    }


    getHashParams() {
        var hashParams = {};
        var e, r = /([^&;=]+)=?([^&;]*)/g,
            q = window.location.hash.substring(1);
        while (e = r.exec(q)) {
            hashParams[e[1]] = decodeURIComponent(e[2]);
        }
        return hashParams;
    }

    async getMe() {
        await this.spotifyApi.getMe()
             .then(function (data) {
                 console.log('Some information about the authenticated user', data.body);
                 this.setState({user_image:data.body.images[0].url, user_name:data.body.display_name})
                 console.log(this.state);
                 
             }, function (err) {
                 console.log('Erro getMe!', err);
             });
     };

    render(){
        return(
            <div className="FirstPage">
                <img src={this.user_image} ></img>
                <p>Bem vindo {this.user_name}!</p>
                <p></p>
                <p>Suas músicas salvas:</p>
            </div>
        )
    }

};

export default FirstPage;
