import React, { Component, useState } from 'react';

import './styles.css';

var SpotifyWebApi = require('spotify-web-api-node');

function getHashParams() {
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
        q = window.location.hash.substring(1);
    while (e = r.exec(q)) {
        hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
}

const params = getHashParams();

const access_token = params.access_token;
const client_id = params.client_id;
const client_secret = params.client_secret;

var spotifyApi = new SpotifyWebApi({
    clientId: client_id,
    clientSecret: client_secret,
    redirectUri: 'http://www.example.com/callback'
});




// const savedTracks = getSavedTracks();

const FirstPage = () => {
    const [userImage, setuserImage] = useState('');
    const [userName, setuserName] = useState('');
    const [userMusics, setuserMusics] = useState('');
    const [page, setPage] = useState('');
    const [totalPages, setTotalPages] = useState('');

    spotifyApi.setAccessToken(access_token);

    async function getMe() {
       await spotifyApi.getMe()
            .then(function (data) {
                // console.log('Some information about the authenticated user', data.body);
                setuserImage(data.body.images[0].url);
                setuserName(data.body.display_name);
            }, function (err) {
                console.log('Erro getMe!', err);
            });
    };

    async function getSavedTracks(pageNumber) {
        await spotifyApi.getMySavedTracks({
            limit: 10,
            offset: pageNumber
        })
            .then(function (data) {
                console.log('Done!');
                console.log(data);
                setTotalPages(data.body.total);
                setuserMusics(data.body.items);
            }, function (err) {
                console.log('Something went wrong!', err);
            });
    }

    function nextPage() {

        if (page === totalPages) return;

        const pageNumber = page + 20;

        console.log(page);

        getSavedTracks(pageNumber);

    }

    function previousPage() {
        if (page === 0) return;

        const pageNumber = page - 20;

        console.log(page);

        getSavedTracks(pageNumber);
    }

    async function removeTrack(music_id) {
       await spotifyApi.removeFromMySavedTracks([music_id])
            .then(function (data) {
                console.log('Removed!');
            }, function (err) {
                console.log('Something went wrong!', err);
            });

        // console.log(music_id)
    };

getMe();
getSavedTracks();

return (
    <div className="FirstPage">
        <img src={userImage} ></img>
        <p>Bem vindo {userName}!</p>
        <p></p>
        <p>Suas mÃºsicas salvas:</p>

        <div className='music-list'>

            {Object.entries(userMusics).map(([k, v]) => (

                <article key={v.track.id}>
                    <p>
                        <img src={v.track.album.images[1].url} ></img>
                        {/* <button onClick={removeTrack(v.track.id)}>Remover MÃºsica</button> */}
                    </p>
                    <a><strong>{v.track.name}</strong></a>
                </article>

            ))}

        </div>

        <div className='actions'>
                <button  disabled={page === 0} onClick={previousPage()}>Anterior</button>
                <button  disabled={page === totalPages}  onClick={nextPage()}>PrÃ³ximo</button>
        </div>

    </div>
);
};

export default FirstPage;
